function addLabel(tempChart, labelName, tempArray) {
    for (var i = 0; i < tempChart.data.datasets.length; i++) {
        let currentLabelName = tempChart.data.datasets[i].label;
        if (currentLabelName === labelName) {
            return null;
        }
    }
    tempChart.data.datasets.push({ label: labelName, backgroundColor: "rgba(" + Math.floor(Math.random() * 256) + "," + Math.floor(Math.random() * 256) + "," + Math.floor(Math.random() * 256) + ",0.5)", borderColor: "rgba(" + Math.floor(Math.random() * 256) + "," + Math.floor(Math.random() * 256) + "," + Math.floor(Math.random() * 256) + ")", borderWidth: 1, data: tempArray })
    tempChart.update();
}

function updateArray(tempChart, labelName, tempArray, tempHaftalikSarjSayisi) {


    for (var i = 0; i < tempChart.data.datasets.length; i++) {
        let currentLabelName = tempChart.data.datasets[i].label;

        if (currentLabelName === labelName) {
            tempChart.data.datasets[i].data = tempArray;
            break;
        }

    }
    tempChart.update();

}

function findTempHaftalikSarjSayisi(tempChart, labelName) {
    console.clear();
    for (var i = 0; i < tempChart.data.datasets.length; i++) {
        let currentLabelName = [];
        let labelNameArray = [];
        labelNameArray = labelName.split(" ");
        currentLabelName = tempChart.data.datasets[i].label.split(" ");
        console.log(currentLabelName[3]);
        if (currentLabelName[3] === labelNameArray[3]) {
            return parseInt(currentLabelName[3]);
        }
    }
}

